import React, { useState, useEffect, useRef } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { Dialog, DialogContent, DialogTitle, DialogActions, Button, Typography, Card,CardActionArea,CardContent,CardMedia,CardActions} from '@material-ui/core'

const useStyles = makeStyles((theme) => ({
  form: {
    marginTop: theme.spacing(1),
  },
}));


export default function Popup(props) {

  const { openProjectPopup, setOpenProjectPopup, project} = props
 


  const classes = useStyles();
  const test = e => {
    e.preventDefault();
  }


  return (

    <Dialog open={openProjectPopup}
      fullWidth
    >
      <DialogTitle>
        
      </DialogTitle>
      <DialogContent>
      <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          component="img"
          alt="myProject"
          image={project.imageUrl}
          title={project.title}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {project.title} - {project.type}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
           {project.description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" onclick={project.link} color="primary">
          Go to Project
        </Button>
      </CardActions>
    </Card>


        
      </DialogContent>


      <DialogActions>
        <Button onClick={() => setOpenProjectPopup(false)} color="primary">
          Close
          </Button>

      </DialogActions>
    </Dialog >
  )
}
