import React, { useState, useEffect } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';


import axios from 'axios';
import { API_ENDPOINT } from '../config';
i
const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));


const SignIn = (props) => {
  const classes = useStyles();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [action, setAction] = useState("Sign In");
  const [errormsg, setMessage] = useState("");


  const authAxios = axios.create({
    baseURL: API_ENDPOINT,
    headers: {
    },
  });
  const authenticate = async () => {
    try {
      localStorage.removeItem("token")
      const result = await authAxios.post(`/account/login`, {
        userName: username,
        password: password
      });
      const tokens = await (result.data.token)
      localStorage.setItem('token', tokens);
      window.location.reload();
    }
    catch (err) {
      console.log(err.message);
      setMessage(getError(err, true));
    }

  };




  useEffect(() => {
    localStorage.removeItem("token")
    if (props.action) {
      setAction(props.action);
    } else {
      setAction("Sign In");
    }

  }, [props])





  const test = e => {
    e.preventDefault();

  }
  return (

    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          {action}
        </Typography>
        <p style={{ color: 'red' }} id="errorMsg" className="error">{errormsg}</p>
        <form onSubmit={test} className={classes.form}>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            id="username"
            label="User Name"
            name="username"
            autoComplete="username"
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            id="password"
            autoComplete="current-password"
          />

          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            onClick={() => authenticate()}
            className={classes.submit}
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item>


            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={8}>
      </Box>
    </Container>
  );
}
export default SignIn;
